  document.getElementsByName("end_date_mobile")[0].setAttribute('min', start_date)
})



function showTab(n) {
  // This function will display the specified tab of the form...
  var x = document.getElementsByClassName("tab");
  x[n].style.display = "block";
  //... and fix the Previous/Next buttons:
  if (n == 0) {
    document.getElementById("prevBtn").style.display = "none";
  } else {
    document.getElementById("prevBtn").style.display = "inline";
  }
  if (n == (x.length - 1)) {
    document.getElementById("nextBtn").innerHTML = "Réserver";
  } else {
    document.getElementById("nextBtn").innerHTML = "Suivant";
  }
  //... and run a function that will display the correct step indicator:
  fixStepIndicator(n)
}

function showMobileTab(n) {
  // This function will display the specified tab of the form...
  var x = document.getElementsByClassName("tab_mobile");
  x[n].style.display = "block";
  //... and fix the Previous/Next buttons:
  if (n == 0) {
    document.getElementById("prevBtn_mobile").style.display = "none";
  } else {
    document.getElementById("prevBtn_mobile").style.display = "inline";
  }
  if (n == (x.length - 1)) {
    document.getElementById("nextBtn_mobile").innerHTML = "Réserver";
  } else {
    document.getElementById("nextBtn_mobile").innerHTML = "Suivant";
  }
  //... and run a function that will display the correct step indicator:
  fixStepIndicator(n)
}

function nextPrev(n) {
  var start_date = $("#start_date")[0].value;
  var end_date = $("#end_date")[0].value

  // This function will figure out which tab to display
  var x = document.getElementsByClassName("tab");
  // Exit the function if any field in the current tab is invalid:

  // CHECK IF THE FRONT END FORM IS VALID BEFORE CONTINUE, RETRIEVE ERROR ELSE
  var valid = false
  var complete = false
  if(start_date && end_date){
    valid = true
  }

  if(valid){
    // Hide the current tab:
    x[currentTab].style.display = "none";
    // Increase or decrease the current tab by 1:
    currentTab = currentTab + n;
    // if you have reached the end of the form...
    if (currentTab >= x.length) {
      // ... the form gets submitted:
      document.getElementById("checkoutForm").submit();
      console.log("Submited..............................");
      $("#before_submit").hide()
      $("#after_submit").show();
      return false;
    }
    // Otherwise, display the correct tab:
    showTab(currentTab);
  } else {
    alert("Bien vouloir remplir intégralement le formulaire")
  }
}
function fixStepIndicator(n) {
  // This function removes the "active" class of all steps...
  var i, x = document.getElementsByClassName("step");
  for (i = 0; i < x.length; i++) {
    x[i].className = x[i].className.replace(" active", "");
  }
  //... and adds the "active" class on the current step:
  x[n].className += " active";
}

$("#before_submit").show()
$("#after_submit").hide()


