if($("#category_3rooms")[0].checked == false){
  $("#category_3rooms_qty").fadeOut()
  $("#category_3rooms_qty").hide()
}

if($("#category_2rooms")[0].checked == false){
  $("#category_2rooms_qty").fadeOut()
  $("#category_2rooms_qty").hide()
}

$("#category_3rooms").change(function(){
  console.log("category_3rooms changed!!!!!!!")
  if(!($("#category_3rooms")[0].checked)){
    $("#category_3rooms_qty").fadeOut()
    $("#category_3rooms_qty").hide()
  } else {
    $("#category_3rooms_qty").fadeIn()
    $("#category_3rooms_qty").show()
  }
})

